query:
UPDATE claims c
SET 
  compensationAmount = (
    SELECT COUNT(*) FROM passengers p WHERE p.claim_id = c.id
  ) * 400,
  commissionAmount = (
    SELECT COUNT(*) FROM passengers p WHERE p.claim_id = c.id
  ) * 400 * 0.25,
  netPayoutAmount = (
    SELECT COUNT(*) FROM passengers p WHERE p.claim_id = c.id
  ) * 400 * 0.75
WHERE c.id IN (60001, 60002);

